import React from "react";

const Home = () => {
  return (
    <div className="bg-gray-100 min-h-screen p-6 space-y-10">
      {/* Slideshow */}
      <div className="overflow-x-auto whitespace-nowrap scrollbar-hide">
        <div className="flex space-x-4">
          {Array.from({ length: 10 }).map((_, index) => (
            <img
              key={index}
              src="IMAGES HERE!"
              src="IMAGES HERE!"
              src="IMAGES HERE!"
              src="IMAGES HERE!"
              src="IMAGES HERE!"
              src="IMAGES HERE!"
              src="IMAGES HERE!"
              src="IMAGES HERE!"
              src="IMAGES HERE!"
              src="IMAGES HERE!"
              alt={`Yoga ${index + 1}`}
              className="w-80 h-60 rounded-xl object-cover shadow-lg"
            />
          ))}
        </div>
      </div>

      {/* Yoga Context */}
      <div className="max-w-5xl mx-auto text-center">
        <h2 className="text-3xl font-bold mb-4 text-green-700">Why Yoga?</h2>
        <p className="text-gray-700 text-lg leading-relaxed">
          Yoga is a holistic discipline that strengthens the body, calms the mind, and nurtures the spirit. Practicing yoga regularly can lead to
          improved flexibility, better posture, enhanced breathing, and mental clarity. It reduces stress, anxiety, and even aids in sleep quality.
          Yoga isn't just about physical poses; it also teaches mindfulness, emotional balance, and inner peace. From ancient traditions to modern
          practice, yoga serves as a pathway to self-awareness and wellness. Its benefits extend beyond the matâ€”into daily habits, relationships,
          and personal growth. Whether you're a beginner or seasoned yogi, yoga meets you where you are. With consistent effort, you cultivate
          strength, patience, and resilience. In our fast-paced lives, yoga brings grounding and reflection. It's both a physical workout and a
          mental detox. As you stretch your muscles, you also expand your perspective. Join the yoga journey today and unlock a healthier, happier you.
        </p>
      </div>

      {/* People Showcase */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6 max-w-6xl mx-auto">
        {[
          { name: "MUHAMMED SHUHAIB", age: 20, post: "GRAND MASTER" },
          { name: "AKASH REDDY D", age: 21, post: "CFO" },
          { name: "ROYAL MAJUMDER", age: 21, post: "SENIOR COURSE MANAGER" },
          { name: "MOHAMMED NUMAAN HASAN", age: null, post: "PRODUCT DESIGNER" },
        ].map((person, idx) => (
          <div key={idx} className="bg-white shadow-md rounded-xl p-4 flex flex-col items-center text-center">
            <img
              src="PEOPLE'S PICS HERE"
              src="PEOPLE'S PICS HERE"
              src="PEOPLE'S PICS HERE"
              src="PEOPLE'S PICS HERE"
              alt={person.name}
              className="w-32 h-32 object-cover rounded-full mb-4 border-4 border-green-400"
            />
            <h3 className="text-lg font-bold text-gray-800">{person.name}</h3>
            {person.age && <p className="text-sm text-gray-600">Age: {person.age}</p>}
            <p className="text-sm text-green-700 font-semibold mt-1">Post: {person.post}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
